workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class AmnestyOnlineUpdateListController extends ListController
{
    @Service("AmnestyOnlineUpdateService")
    def svc;

    String serviceName = "AmnestyOnlineUpdateService";
    String entityName = "amnestyonlineupdate";

    Map createPermission = [domain: 'AUDIT', role: 'AUDIT_AMNESTY'];

    void beforeGetColumns(Map params) {
        def state = selectedOption?.state;
        if (state) params.state = state;
    }

    def selectedOption;
    def optionsModel = [
        fetchList: { 
            return svc.getStates();; 
        },
        onselect: {o-> 
            query.state = selectedOption?.state; 
            reloadAll(); 
        }
    ] as ListPaneModel; 
}
