workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class BillingGroupListController extends ListController
{
    @Service('LoanBillingGroupService')
    def svc;

    String serviceName = "LoanBillingGroupService";
    String entityName = "billinggroup";

    Map createPermission = [domain: 'LOAN', role: 'LEGAL_OFFICER,ACCT_ASSISTANT'];

    def selectedOption;
    def optionsModel = [
        getItems: {
            return svc.getStates();
        }, 
        onselect: { o->
            query.state = o.state;
            reloadAll();
        }
    ] as ListPaneModel;

    void beforeGetColumns(Map params) {
        params.state = selectedOption?.state;
    }
}
