import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class AvailBadDebtAmnestyListController extends ListController
{
    @Service("AvailBadDebtAmnestyService")
    def svc;

    String serviceName = "AvailBadDebtAmnestyService";
    String entityName = "availbaddebtamnesty";        

    Map createPermission = [domain: 'LOAN', role: 'CAO_OFFICER,LEGAL_OFFICER'];

    boolean closeButtonAdded = false;

    void beforeGetColumns(Map params) {
        def state = selectedOption?.state;
        if (state) params.state = state;
    }

    def selectedOption;
    def optionsModel = [
        fetchList: { 
            return svc.getStates();
        },
        onselect: {o-> 
            query.state = selectedOption?.state; 
            reloadAll(); 
        }
    ] as ListPaneModel; 
}
