workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

public class BPMasterListReportController extends ReportController  
{
    @Service('BPMasterListReportService')
    def svc; 

    @Service('BusinessPermitTypeService')
    def permitTypeSvc;     

    String title = "Business Taxpayer Masterlist";
    String reportpath = 'com/rameses/gov/etracs/bpls/reports/'
    String reportName = reportpath + 'BPMasterList.jasper'

    def rptparams = [:];

    def formControl = [ 
        getControlList: { 
            return [
                [type:'combo', caption:'Permit Type', name:'entity.permittype', required:true, items:'permitTypes',  expression:'#{item.title}', captionWidth:100, preferredSize:'0,20'],
                [type:'integer', caption:'Year', name:'entity.year', required:true, preferredSize:'100,20', captionWidth:100], 

                [type:'checkbox', caption:'Status', text:'ACTIVE', name:'entity.state_active', immediate:true, preferredSize:'0,20', captionWidth:100 ], 
                [type:'checkbox', caption:'',  text:'PROCESSING', name:'entity.state_processing',immediate:true, preferredSize:'0,20', captionWidth:100 ] 
            ]; 
        } 
    ] as FormPanelModel; 

    def permitTypes = []; 

    def initReport() { 
        def outcome = super.initReport(); 
        permitTypes = permitTypeSvc.getList(); 
        entity.permittype = (permitTypes? permitTypes[0] : null); 
        entity.year = new java.sql.Date( System.currentTimeMillis() ).toString().split("-")[0]; 
        entity.state_active = true;         
        return outcome; 
    } 

    Map getParameters() { 
        def buff = [];
        if ( entity.state_active ) buff << 'ACTIVE'; 
        if ( entity.state_processing ) buff << 'PROCESSING'; 

        rptparams.TXNSTATE = buff.join(','); 
        rptparams.YEAR = entity.year; 
        return rptparams; 
    }

    def getReportData() { 
        return svc.getReport( entity ); 
    }
}
