import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

public class BusinessPermitSummaryReportController extends ReportController 
{
    @Service('BPPermitSummaryReportService')
    def svc;

    String title = "Summary of Business Permit";
    String reportpath = 'com/rameses/gov/etracs/bpls/reports/'
    String reportName = reportpath + 'BusinessPermitSummary.jasper'

    def formControl = [
        getFormControls: {
            return [
                new FormControl( "integer", [caption:'Year', name:'entity.year', required:true, preferredSize:'100,20', captionWidth:100])
            ]    
        },
    ] as FormPanelModel; 

    def getReportData(){
        return svc.getReport( entity ); 
    }
}
