workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import java.rmi.server.*;
import com.rameses.osiris2.reports.*;

class  BusinessBillingReportController  extends com.rameses.etracs.shared.ReportController  {

    @Service("BusinessBillingService") 
    def billingSvc;

    def entity;
    def data;

    String title = "Business Billing";
    String reportpath = "com/rameses/gov/etracs/bpls/reports/billing/";
    String reportName = reportpath + 'Assessment.jasper';

    void viewReport() {
        data = billingSvc.getReport([applicationid:entity.objid]); 
        report.viewReport();
    } 

    def report = [
        getReportName : { return reportPath + "main.jasper" },
        getReportData : { return  data },
        getParameters : { return paramsvc.getStandardParameter() },
        getSubReports : { return [
                new SubReport("billitem", reportPath + "billitem.jasper"),
                new SubReport("billpayment", reportPath + "billpayment.jasper"),
            ] as SubReport[];
        }
    ] as ReportModel
 }
