    @Service('BusinessRequirementTypeService')
    def service;

    String entityName  = 'businessrequirementtype';
    String prefix = "BPREQ";
    def handlers;

    public void onInit() {
        handlers = [];
        try {
            handlers = Inv.lookupOpeners( 'businessrequirementtype:type', [:] );
        }
        catch(e) {;}
    }

}
