import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class RestrictedPropertyListController extends com.rameses.gov.etracs.rpt.report.AsyncReportController
{
    @Service('LandTaxReportRestrictedPropertyService') 
    def svc 

    String title = 'List of Restricted Properties'

    String reportName = 'com/rameses/gov/etracs/landtax/report/listing/restricted_properties.jasper';

    void buildReportData(entity, asyncHandler){
        svc.getRestrictedPropertyListing(entity, asyncHandler)
    }

    Map getParameters(){
        data.parameters.BARANGAY = entity.barangay?.name
        return data.parameters;
    }

   def formControl = [
        getFormControls: {
            return [
                new FormControl( "combo", [caption:'LGU', name:'entity.lgu', items:'lgus', expression:'#{item.name}', preferredSize:'0,21', emptyText:'ALL']),
                new FormControl( "combo", [caption:'Barangay', name:'entity.barangay', items:'barangays', expression:'#{item.name}', depends:'entity.lgu', dynamic:true, preferredSize:'0,21', emptyText:'ALL']),
                new FormControl( "text", [caption:'Section', name:'entity.section', preferredSize:'50,19', depends:'entity.barangay', visibleWhen:'#{entity.barangay != null}']),
            ]    
        },
   ] as FormPanelModel;
}
