import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class RPTDelinquencySummaryController extends com.rameses.gov.etracs.rpt.report.AsyncReportController
{
    @Service('RPTReportDelinquencyService') 
    def svc

    String title = 'Realty Tax Delinquency Summary';
    String reportName = 'com/rameses/gov/etracs/rpt/report/landtax/rptdelinquencysummary.jasper';

    def data;
    def task;
    def msg;

    void buildReportData(entity, asyncHandler){
        entity.reporttype = 'summary';
        svc.generateDelinquencyReport(entity, asyncHandler);
    }

    Map getParameters() {
        return [ TITLE : title ]; 
    }

    def formControl = [
        getFormControls: {
            return [
                new FormControl( "combo", [caption:'LGU', name:'entity.lgu', required:true, items:'lgus', expression:'#{item.name}']) 
            ]    
        } 
    ] as FormPanelModel;
}
