workunit.code:

import com.rameses.rcp.annotations.*;        
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class MunicipalityConsolidationSupportController
{
    @Binding 
    def binding;

    @Service('MunicipalityConsolidationSupportService')
    def svc; 

    def entity;

    void resendToProvince(){
        if (MsgBox.confirm('Resubmit consolidation to Province?')){
            svc.resubmitToProvince(entity);
            MsgBox.confirm('Consolidation has been successfully submitted to province.')
        }
    }

    void resendRequirements(){
        if (MsgBox.confirm('Resubmit consolidation requirements to Province?')){
            svc.resubmitRequirements(entity);
            MsgBox.confirm('Consolidation requirements have been successfully submitted to province.')
        }
    }


}
