workunit.code:

import com.rameses.rcp.annotations.*;        
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class MunicipalitySubdivisionSupportController
{
    @Binding 
    def binding;

    @Service('MunicipalitySubdivisionSupport2Service')
    def svc; 

    def entity;

    void resendToProvince(){
        if (MsgBox.confirm('Resubmit subdivision to Province?')){
            svc.resubmitToProvince(entity);
            MsgBox.confirm('Subdivision has been successfully submitted to province.')
        }
    }

    void resendRequirements(){
        if (MsgBox.confirm('Resubmit subdivision requirements to Province?')){
            svc.resubmitRequirements(entity);
            MsgBox.confirm('Requirements have been successfully submitted to province.')
        }
    }


}
