workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class MonthlyAssessmentReportModel extends com.rameses.gov.etracs.rpt.report.AsyncReportController
{
    @Service('RPTReportAssessmentReportService') 
    def svc

    String title = 'Monthly Assessment Report';
    String reportName = 'com/rameses/gov/etracs/rpt/report/assessment_report.jasper'


    def initReport(){
        entity.qtr = null;
        return 'default';
    }

    void buildReportData(entity, asyncHandler){
        svc.buildReport(entity, asyncHandler);
    }

    def valuetypes = ['MV', 'AV']

    def formControl = [
         getFormControls: {
             return [
                 new FormControl( "combo", [caption:'LGU', name:'entity.lgu', items:'lgus', expression:'#{item.name}', required:true, allowNull:false]),
                 new FormControl( "integer", [caption:'Year', name:'entity.year', required:true, preferredSize:'100,19']),
                 new FormControl( "combo", [caption:'Month', name:'entity.month', items:'months', expression:'#{item.caption}', required:true, preferredSize:'100,19']),
                 new FormControl( "combo", [caption:'Value', name:'entity.valuetype', items:'valuetypes', required:true, allowNull:false, preferredSize:'100,19']),
             ]    
         },
    ] as FormPanelModel;

}
