workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;


class AssessmentRollController extends com.rameses.gov.etracs.rpt.report.AsyncReportController
{
    @Service('RPTReportAssessmentRollService') 
    def svc 

    String title = 'Assessment Roll'

    String getReportName(){
        if (entity.taxability == 'TAXABLE')
            return 'com/rameses/gov/etracs/rpt/report/assessmentrolltaxable.jasper';
        return 'com/rameses/gov/etracs/rpt/report/assessmentrollexempt.jasper';
    }

    void buildReportData(entity, asyncHandler){
        if (entity.taxability == 'TAXABLE')
            svc.generateAssessmentRollTaxable( entity, asyncHandler )
        else
            svc.generateAssessmentRollExempt( entity, asyncHandler )
    }

    def formControl = [
        getFormControls: {
            return [
                new FormControl( "combo", [caption:'LGU', name:'entity.lgu', required:true, items:'lgus', expression:'#{item.name}']),
                new FormControl( "combo", [caption:'Barangay', name:'entity.barangay', required:true, items:'barangays', expression:'#{item.name}', depends:'entity.lgu', dynamic:true]),
                new FormControl( "combo", [caption:'Taxability', name:'entity.taxability', items:'taxabilities', required:true, immediate:true]),
                new FormControl( "text", [caption:'Section', name:'entity.section', preferredSize:'50,19']),
            ]    
        },
   ] as FormPanelModel;


    def getTaxabilities(){
        return ['TAXABLE', 'EXEMPT']
    }
}
