workunit.code:


import com.rameses.rcp.annotations.* 
import com.rameses.rcp.common.* 
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class ViewNoaController extends com.rameses.gov.etracs.rpt.report.notice.AssessmentNoticeController
{
    def faas 

    def viewNoa(){
        faas = entity;
        init();
        def notice = svc.getExistingNoticeByFaas([objid:faas.objid]);
        if (notice){
            entity.putAll(notice);
            return preview();
        } else {
            entity.taxpayer = faas.taxpayer;
            def item = svc.getFaasById(faas.objid)
            item.included = true 
            entity.items.add(item)
            if (save() !=  null){
                return preview();    
            }
            return '_close';
        }
    }
}
