workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;


class TMCRController extends com.rameses.gov.etracs.rpt.report.AsyncReportController
{
    @Service('RPTReportTMCRService') 
    def svc 

    String title = 'Tax Mapping Control Roll Report'

    String reportName = 'com/rameses/gov/etracs/rpt/report/tmcr.jasper';

    def initReport(){
        entity.ry = svc.getCurrentRy();
        return 'default'
    }

    void buildReportData(entity, asyncHandler){
        svc.generateTMCR(entity, asyncHandler);
    }

    def getReporttypes(){
        return ['GR', 'MAINTENANCE']
    }

    def formControl = [
        getFormControls: {
            return [
                new FormControl( "combo", [caption:'Report Type', name:'entity.reporttype', emptyText:'ALL', items:'reporttypes', preferredSize:'0,21']),
                new FormControl( "combo", [caption:'LGU', name:'entity.lgu', required:true, items:'lgus', expression:'#{item.name}']),
                new FormControl( "combo", [caption:'Barangay', name:'entity.barangay', required:true, items:'barangays', expression:'#{item.name}', depends:'entity.lgu', dynamic:true]),
                new FormControl( "integer", [caption:'Year', name:'entity.ry', required:true, preferredSize:'50,21']),            
                new FormControl( "text", [caption:'Section', name:'entity.section', preferredSize:'50,19', required:true]),
            ]    
        },
   ] as FormPanelModel;

    Map getParameters(){
        data.parameters.SECTION = entity.section;
        return data.parameters;
    }


}
