workunit.code:



import com.rameses.rcp.annotations.* 
import com.rameses.rcp.common.* 
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.util.MapBeanUtils;

public class CancelAnnotationController extends PageFlowController
{

    @Binding
    def binding;

    @Service('CancelAnnotationService')
    def svc 

    @Invoker
    def invoker 

    def MODE_CREATE = 'create';
    def MODE_EDIT   = 'edit';
    def MODE_READ   = 'read';

    def STATE_INTERIM     = 'INTERIM';
    def STATE_FORAPPROVAL = 'FORAPPROVAL';
    def STATE_APPROVED    = 'APPPROVED';
    def STATE_CANCELLED   = 'CANCELLED';


    def mode;
    def entity;
    def annotation;


    def init(){
        entity = svc.init(annotation)
        mode = MODE_CREATE
        return super.signal('init')
    }


    def open(){
        entity = svc.open(entity)
        mode = MODE_READ;
        return super.signal('open');
    }

    boolean getAllowEdit(){
        if ( entity.state == STATE_APPROVED ||
             entity.state == STATE_CANCELLED )
                return false;
        if (mode == MODE_READ )
                return false;
        return true 
    }



    /*-----------------------------------------------------
     * 
     * DOCUMENT SUPPORT 
     *
     *----------------------------------------------------*/
    def oldEntity 

    void edit(){
        oldEntity = MapBeanUtils.copy(entity)
        mode = MODE_EDIT;
    }


    void cancelEdit(){
        if (oldEntity){
            entity.putAll(oldEntity)
        }
        oldEntity = null
        mode = MODE_READ;
    }

    void save(){
        if (mode == MODE_CREATE)
            entity = svc.create(entity)
        else 
            entity = svc.update(entity)
        mode = MODE_READ;
    }    


    void delete(){
        svc.delete(entity)
    }



    /*-----------------------------------------------------
     * 
     * WORKFLOW ACTIONS
     *
     *----------------------------------------------------*/


    void submitCancelAnnotationForApproval(){
        entity = svc.submitForApproval(entity);
    }


    void disapproveCancelAnnotation(){
        entity = svc.disapprove(entity)
    }


    void approveCancelAnnotation() {
        entity = svc.approve(entity);
    }


}
