workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class SpecificClassLookupController extends LookupController
{
    @Service("LandSpecificClassService")
    def svc;

    def state;

    Column[] getColumns() {
        return [
            new Column(name:"code", caption:"Code", width:70),
            new Column(name:"name", caption:"Specific Class", width:250),
        ]
    }

    List fetchList( Map params ) { 
        params.state = ( state ? state : '%')
        return svc.getSpecificClasses( params )
    }
}
