workunit.code:
import com.rameses.rcp.annotations.* 
import com.rameses.rcp.common.* 
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.gov.etracs.rptis.util.RPTUtil;

class ResectionMainInfoController
{
    @Binding
    def binding;

    @Caller
    def caller;


    def svc;

    def MODE_READ = 'read';
    def MODE_EDIT = 'edit';

    def mode;

    def entity;

    String title = 'General Information'


    void init(){
        entity = svc.openResection( entity.objid );
        mode = MODE_READ;
    }


    boolean getAllowEdit(){
        if (entity.state == 'APPROVED') return false;
        if (mode == 'read') return false;
        return true;
    }


    void edit(){
        caller.addMessage([type:'main', msg:'General Info is still in editing mode.']);
        mode = MODE_EDIT;
    }


    void save(){
        entity.putAll( svc.updateResection(entity) );
        caller.clearMessages('main');
        mode = MODE_READ;
    }

    void refresh(){}


    List getQuarters(){
        return [1,2,3,4]
    }

    boolean getShowActions(){
        if (entity.taskstate && entity.taskstate.matches('assign.*')) return false;
        if (entity.state.matches('FORAPPROVAL|FORPROVAPPROVAL|APPROVED')) return false;
        if (mode != MODE_READ) return false;
        return true;
    }

}
