import com.rameses.rcp.annotations.*;
import com.rameses.seti2.models.*;

class AFInventoryList extends CrudListModel {

     @PropertyChangeListener
     def listener = [
         "query.af" : { o->
             reload();
         }
     ]          

     def getCustomFilter() {
         if( query.af ) {
             return ["objid = :afid", [afid: query.af.objid] ];
         }
         else {
             return null;
         }
     }
}
