class Cashbreakdown  {
     def entity;
     def handler;
     boolean editable = false;
     boolean showCreditMemos = true;
     boolean showCashBreakdown = true;

     void init() {;}

     def checkModel = [
         fetchList: { o->
             return handler.getChecks();
         }
     ] as BasicListModel;

     def creditMemoModel = [
         fetchList: { o->
             return handler.getCreditMemos();
         }
     ] as BasicListModel;

     def doOk() {
         return "_close";
     }

     def doCancel() {
         return "_close";
     }
}
