import  com.rameses.seti2.models.*;
class LiquidationFundList extends CrudSubListModel {

    public def getCustomFilter() {
        return [ " " + tag + " = :pid", [ pid: masterEntity.objid ]  ];
    }

    public def open() {
        def op = super.open();
        op.target = 'popup';
        return op;
    }

}
