workunit.code:

import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import java.rmi.server.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;
import com.rameses.osiris2.reports.*;

class AFRemittanceCashReceiptCController {

    @Service("AFRemittanceService")
    def service;

    def title;
    def entity;

    void init() {
        title = (entity.txnno) ? "Remittance" : "Unremitted (Current AF Inventory)";
    }

    def receiptsListModel = [
        fetchList: {
            if( entity.txnno )
                return service.getRemittedAF( [objid:entity.objid] );
            else
                return service.getUnremittedAF( [:] );
        }
    ] as BasicListModel;

}
