workunit.code:

import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import java.rmi.server.*;

class PaymentOptionController  {

    @Binding
    def binding;

    String title = "Payment Option";
    def handler;

    def payOption = "FULL";
    def date;
    def numdays;
    def amount;

    def doOk() {
        if(!handler) 
            throw new Exception("Pls. include handler in payoption");

        def qry = [payoption: payOption];
        switch( payOption ) {
            case 'DATE':
                qry.date = date;
                break;
            case 'NUMDAYS':
                qry.numdays = numdays;
                break;
            case 'SPECIFYAMOUNT':
                qry.amount = amount;
                break;
        }
        handler(qry);
        return "_close";
    }

    def doCancel() {
        return "_close";
    }
}
