import com.rameses.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.common.*;
import com.rameses.functions.*;

class SpecifyBillDate {

    def fromdate;
    def dateoption = "specify-date";
    def todate;
    int numdays;
    def month;
    def handler;
    def df = new java.text.SimpleDateFormat("yyyy-MM-dd");



    def doOk() {
        def x = fromdate; 
        if( dateoption == "specify-date") {
            x = todate;
        }
        else if( dateoption == "month" ) {
            MsgBox.alert("month " + month);
            def cal = Calendar.instance;
            cal.setTime(fromdate);
            MsgBox.alert("year " + cal.get(Calendar.YEAR));
            MsgBox.alert("month " + cal.get(Calendar.MONTH)+1);
            throw new Exception("stop");
        }
        else {
            if( numdays <= 0 ) throw new Exception("Numdays is required");
            def d  = DateFunc.getDayAdd( fromdate, numdays );
            x = df.format(d);
        }
        //calculate the bill date
        handler( x );
        return "_close";
    }

    def doCancel() {
        return "_close";
    }

}
