import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.common.*;
import java.rmi.server.*
import com.rameses.util.*;

class  MarketAccountController extends MasterCRUDController {

    String serviceName = "MarketAccountService";
    String entityName  = 'marketaccount';
    String prefixId    = 'MACCT';

    Map createEntity() {
         return [rentalunit:[:], owner:[:]];
    }

    def getLookupOpenRentalUnits() {
        return Inv.lookupOpener( "market_rentalunit:lookup", ["query.available":true] );
    }

    void computeRate() {
        def r = service.computeRate( entity );
        entity.rate = r.rate;
    }
}
