workunit.code:

import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import java.rmi.server.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;
import com.rameses.osiris2.reports.*;

class BillingReportController {

    @Binding
    def binding;

    @Service("MarketBillingService")
    def svc

    @Service("ReportParameterService")
    def paramsvc

    def reportdata;
    def reportPath = "com/rameses/gov/etracs/market/report/billing/"

    def entity 

    void viewReport() {
        reportdata = svc.getReport( entity )
        report.viewReport();
    }

    def report = [
        getReportName : { return reportPath + 'marketbilling.jasper' },
        getReportData : { return reportdata }, 
        getParameters : { return paramsvc.getStandardParameter() ; },
        getSubReports : {
            return [ 
                new SubReport("MarketBillingItem", reportPath + "marketbillingitem.jasper") 
            ] as SubReport[];    
         }
    ] as ReportModel

}
