workunit.code:

import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.seti2.models.*;
import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;
import com.rameses.gov.etracs.vehicle.models.*;

class MbopApplication extends VehicleApplicationForm {
    def engineListModel = [
        fetchList: { o->
            if(entity.info.engines==null) entity.info.engines = [];
            return entity.info.engines;
        },
        addItem: { o->
            entity.info.engines << o;
        },
        onRemoveItem: { o->
            entity.info.engines.remove(o);
        }
    ] as EditorListModel;
}
