import com.rameses.seti2.models.*; 

class WaterworksAccountListModel extends CrudListModel {

    public void initColumn( def c ) {
        if ( c.name == 'sector.code' ) {
            c.visible = false; 
        } else if ( c.name == 'zone.code' ) {
            c.visible = false; 
        } 
    } 
}
