import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.seti2.models.*;

class ReadingGroupController extends SinglePageCrudFormModel{

    def title='Reading Group';
    String schemaName='waterworks_readinggroup';

    def getLookupUser(){
         def h = {o ->
             entity.assignto = [objid:o.objid, name: o.firstname+" "+o.lastname];
             binding.refresh("entity.*");
         }
         return Inv.lookupOpener("user:lookup",[onselect:h]);
    }

}
