import com.rameses.rcp.annotations.*;
import com.rameses.seti2.models.*;

class IndividualOptionViolationList extends CrudListModel {

    @Caller
    def caller;

    String getCustomFilter() {
        return "violator.objid=:entityid";
    }

    def getCustomFilterParams() {
        return [entityid:caller.entity.objid]
    }

}
