import com.rameses.rcp.annotations.*;
import com.rameses.seti2.models.*;

class IndividualOptionIdentificationList extends CrudListModel {
    @Caller
    def caller;

    def getEntityid() {
        return caller?.entity?.objid; 
    } 

    def getCustomFilter() {
        return ["entityid=:entityid", [entityid:caller.entity.objid]];
    }
}
