workunit.code:

import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;
import java.rmi.server.UID;

public class IndividualEntityController  {

    @Binding
    def binding;

    @FormTitle
    def title;

    @Service("IndividualEntityService")
    def service;

    def entity;
    def optionList;
    def selectedOption;
    def initiator = 'edit';

    void initList() {
        optionList = [];
        optionList.addAll( Inv.lookupOpeners( "individualentity:option", [entity: entity] ));
        optionList.addAll( Inv.lookupOpeners( "entity:option", [entity: entity] ));                
        if(optionList.size() > 0 ) {
            selectedOption = optionList[0];
        }
    }

    void refresh() {
        title = entity.name;
        optionList.each {
            try {
                it.handle.refresh();
            }
            catch(e){;}
        }
    }

    void open() {
        entity = service.open( [objid: entity.objid, type:'INDIVIDUAL']);
        refresh();
        initList();
    }

    def remove() {
        if( MsgBox.confirm("You are about to remove this record. Continue?")) {
            service.removeEntity( entity );
            return "_close";
        }
    }

}
