import com.rameses.seti2.models.*;
import com.rameses.rcp.common.*;

public class BankAccountLookup extends CrudLookupModel {
    def fundid;

    def getCustomFilter() {
        if( fundid ) {
            return [ " fund.objid = :fundid", [fundid: fundid] ]; 
        }
        return null;
    }
}
