workunit.code:


import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class CollectionQuickView  {

   String title = "Collection Process Quick View";

   def openAction( def r ) {
        def op = Inv.lookupOpener( r );
        op.target = 'window';
        return op;
   } 

   def requestAF() { return openAction("afrequest:create"); }
   def issueReceipt() { return openAction("cashreceipt:collector:create"); }
   def issueSubcollectorReceipt() { return openAction("cashreceipt:subcollector:create"); }
   def remit() { return openAction("unremitted_collection:collector:view"); }
   def acceptRemittance() { return openAction("unliquidated_remittance:view"); }
   def liquidate() { return openAction("liquidation:create"); }
   def acceptLiquidation() { return openAction("liquidation_list:view"); }
   def deposit() { return openAction("cashdeposit:create"); }

}
