import com.rameses.enterprise.treasury.models.*;
import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;

class PaymentOrderSelection extends AbstractTypeSelectionModel {

    def getList() {
        def m = [ _schemaname: 'paymentordertype' ];
        m.orderBy = 'sortorder';
        m.where = ["NOT(objid IS NULL)"];
        m.select = "objid,caption:{title},handler";
        return qryService.getList(m);
    }

    def openItem(def o) {
        return Inv.lookupOpener( "paymentorder:" + o.handler, [txntype: o] ); 
    }        
}
