workunit.code:

import com.rameses.seti2.models.*;
class RemittanceFundList extends CrudSubListModel {
    public def getCustomFilter() {
        if( tag == "remittance" ) {
            return [ "remittanceid =:id", [id: masterEntity.objid] ];
        }
        else {
            return [ "liquidationfundid =:id", [id: masterEntity.objid] ];
        }
    }
}
